(function ($) {
    $.playSound = function () {
        var audio = $('<audio aaa  style="display:none; " ' + arguments[1] + '>'
            + '<source src="' + arguments[0] + '.mp3" />'
            + '<source src="' + arguments[0] + '.ogg" />'
            + '<embed src="' + arguments[0] + '.mp3" hidden="true"   loop="false" class="playSound" />'
          + '</audio>');
        audio.appendTo('body');
        $(audio)[0].play();
        return true;
    };

    $.stopSound = function () {
        $('[aaa]').stop();
    };

    $.removeSound = function () {
        $('audio').remove();
    };

})(jQuery);